mapper.update:
UPDATE BROAD_CENTER
SET NOTICE_CONTENT = #{content}
WHERE MNO = #{mno}

UPDATE BROAD_CENTER
SET NOTICE_CONTENT = NULL
WHERE MNO = #{mno}

UPDATE BOARD
SET BCOUNT = BCOUNT+1
WHERE BNO=#{bno}

mapper.select:
SELECT BB.BBNO
FROM BRO_BOARD BB, BROAD_CENTER BC
WHERE BC.BCNO = BB.BCNO
AND BC.MNO = #{smno}
AND BB.BBTYPE= #{type}

mapper.update:
UPDATE BOARD
SET BTITLE = #{title}, BCONTENT=#{content}, MODIFY_DATE=SYSDATE
WHERE BNO = #{bno}

mapper.delete:
UPDATE BOARD
SET B_STATUS = 99
WHERE BNO = #{bno}
